import React, {useState} from "react";
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import "../ItemDetailContainer/itemCount.css";
import { useEffect } from "react";


const ItemCount = ({stock, initial, counOfItemCount}) =>{
    
  
    const [count, setCount] = useState (initial);
    
    useEffect(()=>{
    
    const mensajeroCount = () => {
        counOfItemCount(count)
    } 

    const addCountClick =() => {
        if (count < stock ){
            setCount(count + 1);
        }
        
    };

    const removeCountClick =() => {
        if (count > 1 ){
            setCount(count - 1);
        }
       
    };
})

    return (

        <>
            <div className="countCard">
                <RemoveIcon onClick={removeCountClick}/>
                <p>{count}</p>
                <AddIcon onClick={addCountClick}/> 
                <button onClick={mensajeroCount}>aja</button>
            </div>

            
        </>
    )
}

export default ItemCount
