    const {productId} = useParams
 

    useEffect(()=>{
        const detallesProducto= fetch(`https://fakestoreapi.com/products/${productId}`)
        .then(res => res.json())
    
    detallesProducto.then(res=>{
            setDetalles(res)
        });
    }, [] )

    return(
        <>
        <ItemDetail detalles={detalles} />
        </>
    )
}

export default ItemDetailContainer
