import React, {useEffect, useState} from "react";

const ItemDetail =({detalles})=>{

    const [imagen, setImagenes] = useState([]) 
    const imagenProducto= fetch(detalles.images[0])
    .then(res => res.json())
    
    useEffect(()=>{

    
    imagenProducto.then(res=>{
            setImagenes(res)
        });
    }, [] )


    return(
        <>
        <h1>Aqui esto es {detalles.id} y </h1>
        <img src={imagen} alt="" />
        
        </>
    )

}

export default ItemDetail
