import {db} from "../../firebase/firebase";
import {getDocs, collection, query, where, getDoc} from "firebase/firestore"



const ItemDetailContainer=()=>{
 
    const [product, setproduct] = useState([]) 
    let {productId} = useParams()
    console.log(productId)

    useEffect(()=>{

        const productsColletions = collection(db, "products");
        const q= query(productsColletions, where("id", "==", `${productId}`));

        getDoc(productsColletions)
        .then(result=>{
            const elproducto = product.data()
            return elproducto
        }
            )
    
